import './App.css';
import { get_users } from './graphql/queries';
import { useQuery } from '@apollo/client';
function App() {
  const {loading, error, data}=useQuery(get_users)
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <div className="App">
      <h1>Users</h1>
      {data.map(({nom,prenom,age})=>(
          <div>
           <p>{nom}</p>
           <p>{prenom}</p>
           <p>{age}</p>
           </div>
      ))}
    </div>
  );
}

export default App;
