import { gql } from "@apollo/client";


export const ajoutuser=gql`
mutation  adduser($nom:String!,$prenom:String!,$age:String!){
    adduser(nom:$nom,prenom:$prenom,age:$age){
        nom,
        prenom,
        age
    }
}
`;
export const upditeuser=gql`
mutation  adduser($nom:String!,$prenom:String!,$age:String!){
    adduser(nom:$nom,prenom:$prenom,age:$age){
        nom,
        prenom,
        age
    }
}
`;
